 DECLARE @FormNames VARCHAR(MAX) = 'CoreDast'  
 
DECLARE @FormCollectionId INT
  
 UPDATE DocumentCodes   SET FormCollectionId = NULL   
 WHERE DocumentCodeId = 1651
 
 
 DELETE FROM FormItems   
 WHERE FormSectionId IN (SELECT FormSectionId FROM FormSections   WHERE FormId IN (SELECT FormId FROM Forms WHERE FormName IN( SELECT Token FROM [dbo].[SplitString] (@FormNames,','))))    
 
 DELETE FROM FormSectionGroups   WHERE FormSectionId IN (SELECT FormSectionId FROM FormSections   WHERE FormId IN (SELECT FormId FROM Forms WHERE FormName IN( SELECT Token FROM [dbo].[SplitString] (@FormNames,','))))    
 
 DELETE FROM FormSections   WHERE FormId IN (SELECT FormId FROM Forms WHERE FormName IN( SELECT Token FROM [dbo].[SplitString] (@FormNames,',')))    
 
 DECLARE @FormCollectionIds VARCHAR(MAX)    
 Select @FormCollectionIds = STUFF( (SELECT ',' + CAST(F1.FormCollectionId AS VARCHAR(MAX))      FROM FormCollectionForms F1      
 Where F1.FormId IN (SELECT FormId FROM Forms WHERE FormName IN( SELECT Token FROM [dbo].[SplitString] (@FormNames,',')))      FOR XML PATH('') ), 1,1,'')       FROM Forms F    WHERE FormId IN (SELECT FormId FROM Forms WHERE FormName IN( SELECT Token FROM [dbo].[SplitString] (@FormNames,',')))      
 
 DELETE FROM FormCollectionForms  WHERE FormId IN (SELECT FormId FROM Forms WHERE FormName IN( SELECT Token FROM [dbo].[SplitString] (@FormNames,',')))    
 
 DELETE FROM FormCollections  WHERE FormCollectionId IN (SELECT Token FROM [dbo].[SplitString] (@FormCollectionIds,','))    
 
 DELETE FROM Forms   
 WHERE FormId IN (SELECT FormId FROM Forms WHERE FormName IN( SELECT Token FROM [dbo].[SplitString] (@FormNames,',')))
 
 DECLARE @FormId51324 INT INSERT INTO Forms(FormName, TableName, TotalNumberOfColumns, Active, RetrieveStoredProcedure, JavascriptFilePath) values('CoreDast','DocumentDASTs',2,'Y','ssp_GetDocumentDasts','Modules/DAST/Scripts/Dast.js') SET @FormId51324 = SCOPE_IDENTITY()    
 
 DECLARE @FormCollectionId51324 INT    
 SET @FormCollectionId51324 = SCOPE_IDENTITY()    
 
 VALUES (@FormCollectionId51324, @FormId51324, 'Y', 1);
 
